import produce from 'immer';

const CompaniesInitialState={
    company:{},
    companies:[]   
}


export const CompaniesReducer=produce((state,action)=>{
    switch(action.type)
    {
         case "GET_COMPANY_BY_NAME_AND_PASSWORD":state.company=action.paylod;break;
         case "ADD_COMPANY":state.companies.push(action.payload);break;
         case "GET_ALL_COMPANIES":state.companies.push(action.payload);break;
         case "UPDATE_COMPANY":state.companies.push(action.payload);break;
         case "REMOVE_COMPANY":state.companies.push(action.payload);break;
         
      
         
    }
},CompaniesInitialState)
