import React from "react";
import  {useState} from 'react';
import { useDispatch } from "react-redux";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import {addCustomer} from '../Redux/Customer/CustomersThunk'
//import {store} from '../Redax/Reducers';
import { Params } from "react-router";
import { render } from "react-dom";
import { MDBAlert } from 'mdbreact';

const FormPage = (Props) => {
  const dispatch = useDispatch();
    const [errors, setErrors]=useState({id:"שדה חובה", pass:"שדה חובה"});
     const [customer, setCustomer]=useState(!Props.customer?{}:Props.customer);
     const newCustomer=async()=>{
      if(customer.CustomerName&&customer.CustomerPassword)
      {await addCustomer(dispatch, customer.CustomerName,customer.CustomerId ,customer.CustomerPassword, customer.CustomerCreditcard);
        console.log(a);
       alert("wellcome");
       //globalThis.location.href='http://localhost:3000/products';
      }
      else
      {
     //  איך אני משנה ניתוב לקומפוננטת אלרט בלי לחיצה??????????
       alert("תעודת זהות וסיסמא הינם שדות חובה על מנת להירשם");
       
     }
   
  
     
//בדיקת תקינות של שם
    const validateName=(event)=>{

        if(!event.target.value)
        setErrors({...errors, CustomerName:"שדה חובה"});
        else if (event.target.value.includes(" "))
        setErrors({...errors, CustomerName:"אין לשים רווח"});
    else {
        setErrors({...errors, CustomerName:"תקין!"});
    }
    setCustomer({...customer, CustomerName: event.target.value })
    }

//בדיקת תקינות של סיסמא
    const validatePass=(event)=>
{
    
var passw = /^[A-Za-z]\w{7,14}$/;
if (event.target.value.match(passw)) {
  setErrors({...errors, pass: "תקין!"} );
}
else {
  setErrors({...errors, pass: 'Wrong...!! password password between 7 to 16 characters which contain only characters, numeric digits, underscore and first character must be a letter' })
}
setCustomer({...customer, CustomerPassword: event.target.value })

}
render();{
return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">Sign up</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
          Your name
        </label>
        <input type="text" id="defaultFormRegisterNameEx" value={customer.CustomerName} onChange={(e)=>{validateName(e)}} className="form-control" />
        <p > {errors.CustomerName}</p>
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
          Your id
        </label>
        <input type="text" id="defaultFormRegisteridEx" value={customer.CustomerId} className="form-control" />
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
          Your creaditCard
        </label>
        <input type="text" id="defaultFormRegistercreaditCardEx" value={customer.CustomerCreditcard} className="form-control" />
        <p > {errors.CustomerPassword}</p>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
          Your password
        </label>
        <input type="password" id="defaultFormRegisterPasswordEx" value={customer.CustomerPassword} onChange={(e)=>{validatePass(e)}} className="form-control" />
        <div className="text-center mt-4">
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newCustomer()}>
          Register
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};
};
};
export default FormPage;
