        <MDBCardBody>
          <MDBCardTitle>{p.nameProduct} </MDBCardTitle>
          <MDBCardText>
          </MDBCardText>
          <p className='text-muted'>מידה:{p.sizeProduct}</p>
         
          <p className='text-muted'>במלאי:{p.amountProduct}</p>
          <MDBBtn href="#">הוסף לסל</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  )
}
