import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { MDBTable, MDBTableHead, MDBTableBody, MDBBtn } from 'mdb-react-ui-kit';
import { Link } from "react-router-dom";
import {getAllCustomersByCompany} from '../Redux/Customers/CustomersThunk'
const MyCustomers = () => {
  let Company = useSelector((store) => {return store.Companies.company }); 
  const dispatch = useDispatch();
  const [customers, setCustomers] = useState(useSelector((state) => { return state.customers.customer }))

  useEffect(async () => {
    let C = await getAllCustomersByCompany(dispatch,Company.codeCompany);
    await console.log(C);
    if (C) {
        setCustomers(C)
    }
  }, [])

  
 return <>
    <NavManage />
    <h1>הלקוחות שלי</h1>
    <div className="square border justify-content-center">
      <MDBTable striped>
        <MDBTableHead>
          <tr>
            <th scope='col'>#</th>
            <th scope='col'>שם:</th>
            <th scope='col'>אימייל:</th>
            <th scope='col'>פרופיל אחרון:</th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {customers === undefined ? <h6>wait...</h6> :
            customers.map((f, i) =>

              <tr>
                <th scope='row'>{++i}</th>
                <td>{f.NameCustomer}</td>
                <td>{f.EmailCustomer}</td>
                <td>{f.ProfileCustomer}</td>
                
                < td>
                  <div class="d-grid gap-2 d-md-flex justify-content-md">
                    <Link ><MDBBtn href='#'>עדכון</MDBBtn></Link>
                    
                  </div>
                </td>
                
              </tr>
            )}
        </MDBTableBody>
      </MDBTable>
    </div>
    

    <div class="d-flex justify-content-evenly">
      <Link ><MDBBtn href='#'>פיענוח</MDBBtn></Link>
    </div>
  </>
}
export default MyCustomers;
