 import {useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  NavLink,
  
} from "react-router-dom";
  

 export default function ShowCategories() {
  const M = useSelector((store) => {return store.customers.isMenneger });
    const dispatch = useDispatch();
    let myCategoriesFromServer
    let myCategorie,myUpdateCategorie,myAddCategorie,myRemovecategorie;
