      else
        return []
    });

    useEffect(async () => {

        myCategoriesFromServer = await getAllCategoriesFromServer(dispatch);
       
        
        console.log("myCategoriesFromServer");
        console.log(myCategoriesFromServer);
      
  
    }, [])
    console.log("2222")
     console.log(categoriesList)
     debugger
    return <>
    <h2>קטגוריות</h2>
    
    {
     categoriesList.map((item) =>
     
     <div class="card" style={pStyles}>
     
     <div class="card-body">
         <h5 class="card-title">{item.nameCategories}</h5>
         <p class="card-text">
             Some quick example text to build on the card title
             <br></br> and make up the bulk of the
             card's content.
         </p>
         
          <NavLink to={`/categorieUpdate/${item.idCategories}`}>עדכון
         </NavLink> 
         
        
         <NavLink style={nstyle} to={`/ProductDelete/${item.idCategories}`}>מחיקה 
         </NavLink>
      
      


     </div>

         
</div>
          
          
     )   
        // : <div>אין לקוחות</div>

    }
  </>
  
}
