export function GetCustomer(customer) {
    return { type: "GET_CUSROMER",payload:customer }
}
export function GetCustomerId(customer) {
    return { type: "GET_CUSROMER_Id",payload:customer }
}
export function GetAllCustomers(customer) {
    return { type: "GET_ALL_CUSTOMERS",payload:customer }
}
export function AddCustomer(customer) {
    return { type: "ADD_CUSTOMER",payload:customer }
}
export function UpdateCustomer(customer) {
    return { type: "UPDATE_CUSTOMER",payload:customer }
}
export function isMenneger(value) {
    return { type: "IS_MENNEGER",payload:value }
}

//פונקציות השיכות לסל קניות
export function AddToBasket(value) {
    return { type: "ADD_TO_BASKET",payload:value }
}
export function NewBasket(value) {
    return { type: "NEW_BASKET",payload:value }
}
export function UpdateProductFromBasket(value) {
    return { type: "UPDATE_PRODUCT_FROM_BASKET",payload:value }
}
export function DeletProductFromBasket(value) {
    return { type: "DELET_PRODUCT_FROM_BASKET",payload:value }
}
