import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
 import  {useState} from 'react';
 import { useDispatch } from "react-redux";
import {updateCustomer,getCustomerFromserverById} from '../Redux/Customer/CustomersThunk'
 import { useParams } from "react-router";
 import { useEffect } from "react";
 import { useSelector } from "react-redux";

const CustomerUpdate = () => {
    const C = useSelector((store) => {return store.customers.customer });
    console.log(C);
    console.log("id");
    console.log(C.idCustomer)
    const dispatch=useDispatch();
   // let c=code.match.params.id;
    
    let [c2,setC2]=useState({});

   useEffect(async () => {
     debugger;
    let c1=await getCustomerFromserverById(dispatch,C.idCustomer); 
    alert(c1.IdCustomer);
    console.log("c1")
    console.log(c1)
    setC2(c1);
   }, [])



   
   const newCustomer=async(dispatch)=>{
    
    let IdCustomer=document.getElementById("c1").value
    let NameCustomer=document.getElementById("c2").value
    let PasswordCustomer=document.getElementById("c3").value
    let CreditcardCustomer=document.getElementById("c4").value
  
    
   if(IdCustomer)
   {
   
    
    const customer=await updateCustomer(dispatch,IdCustomer,NameCustomer ,PasswordCustomer,CreditcardCustomer);
    console.log(customer)
    alert("הפרטים עודכן בהצלחה");
     return customer
   }
   else
   {
  
    alert("אחד מהפרטים אינו נכון אנא מלא בשנית")
    
  }
  

  }
const up=(e)=>{
    setP(e.target.value)
}
return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">עדכון לקוח</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text" >
          Customer id
        </label>
        <input type="text" id="c1" value={c2.IdCustomer}  className="form-control" name= "c11" onChange={(e)=>up(e)} />
    
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
        Customer name
        </label>
        <input type="text" id="c2" value={c2.nameCustomer} className="form-control" name= "c22" onChange={(e)=>up(e)}/>
        <br />
        <label htmlFor="defaultFormRegistercreaditCardEx" className="grey-text">
        Customer password
        </label>
        <input type="text" id="c3" value={c2.PasswordCustomer} className="form-control" name= "c33" onChange={(e)=>up(e)}/>
        <br />
        <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
        Customer Creditcard
        </label>
        <input type="text" id="c4" value={c2.CreditcardCustomer} className="form-control" name= "c44" onChange={(e)=>up(e)}/>
        <div className="text-center mt-4"></div>
        <br />
        
        <br />
        
        
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newCustomer(dispatch)}>
          עדכון לקוח
                </MDBBtn>
        
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default CustomerUpdate;
