import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { useState } from 'react';
import { useDispatch } from "react-redux";
import { updateCompany ,getCompanyByCode} from '../Redux/Companies/CompaniesThunk'
import { MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import { useEffect } from "react";
import { useSelector } from "react-redux";

export default function CompanyUpdate (codeCompany){
  console.log("code")
  console.log(codeCompany)
  const [modal14, setModal14] = useState(true);
  const [errors, setErrors] = useState({ PrivateEmail: "שדה חובה", UserName: "שדה חובה", UserPassword: "שדה חובה" });
  const [company, setCompany] = useState(!codeCompany.company ? {} : codeCompany.company);
  const dispatch = useDispatch();
  let [c2,setC2]=useState({});
  
  useEffect(async () => {
    debugger
   let c1=await getCompanyByCode(dispatch,codeCompany); 
   console.log("c1")
   console.log(c1)
   setC2(c1);
  }, [])


  const updateCompany = async () => {


    let NameCompany = document.getElementById("NameCompany").value
    let PrivateEmail = document.getElementById("PrivateEmail").value
    let CustomerServiceEmail = document.getElementById("CustomerServiceEmail").value
    let CustomerServiceEmailPassword = document.getElementById("CustomerServiceEmailPassword").value
    let UserName = document.getElementById("UserName").value
    let UserPassword = document.getElementById("UserPassword").value

    if (PrivateEmail && UserName && UserPassword) {

      const company = await updateCompany(dispatch, NameCompany, PrivateEmail, CustomerServiceEmail, CustomerServiceEmailPassword, UserName, UserPassword);
      alert("הפרטים עודכנו בהצלחה");
      setModal14(!modal14);
      return company
    }
    else {

      alert("אנא מלא את כל השדות ");

    }
  }

  //בדיקת תקינות של שם
  const validateName = (event) => {

    if (!event.target.value)
      setErrors({ ...errors, UserName: "שדה חובה" });
    else if (event.target.value.includes(" "))
      setErrors({ ...errors, UserName: "אין לשים רווח" });
    else {
      setErrors({ ...errors, UserName: "תקין!" });
    }
    setCompany({ ...company, UserName: event.target.value })
  }

  //בדיקת תקינות של סיסמא
  const validatePass = (event) => {

    var passw = /^[A-Za-z]\w{7,14}$/;
    if (event.target.value.match(passw)) {
      setErrors({ ...errors, pass: "תקין!" });
    }
    else {
      setErrors({ ...errors, pass: 'Wrong...!! password password between 7 to 16 characters which contain only characters, numeric digits, underscore and first character must be a letter' })
    }
    setCompany({ ...company, UserPassword: event.target.value })
  }

  const up=(e)=>{
    setC2(e.target.value)
}
  return (
    <>
      
      <MDBContainer>
        <MDBModal isOpen={modal14} toggle={() => { setModal14(!modal14) }} centered>
          <MDBModalHeader toggle={() => { setModal14(!modal14); }}>עדכון פרטים</MDBModalHeader>
          <MDBModalBody>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <form>

                    <div className="grey-text">
                      <label>Your Company Name</label>
                      <MDBInput  id="NameCompany" value={c2.NameCompany} onChange={(e)=>up(e)} icon="user" group type="text" validate error="wrong"
                        success="right" />
                        <label>Your Private email</label>
                      <MDBInput  id="PrivateEmail" value={c2.PrivateEmail} onChange={(e)=>up(e)}  icon="envelope" group type="email" validate error="wrong"
                        success="right" />
                         <label>Your Customer service email</label>
                      <MDBInput id="CustomerServiceEmail" value={c2.CustomerServiceEmail} onChange={(e)=>up(e)} icon="envelope" group type="email" validate error="wrong"
                        success="right" />
                        <label>Your Customer service email password</label>
                      <MDBInput id="CustomerServiceEmailPassword" value={c2.CustomerServiceEmailPassword} onChange={(e)=>up(e)} icon="lock" group type="password" validate />
                      <label>Your User name</label>
                      <MDBInput  id="UserName" value={c2.UserName} onChange={(e)=>up(e)} icon="user" group type="text" validate error="wrong"
                        success="right" />
                      <label>Your User password</label>
                      <MDBInput  id="UserPassword" value={c2.UserPassword} onChange={(e)=>up(e)} icon="lock" group type="password" validate />
                    </div>
                    <div className="text-center">
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color="secondary" onClick={() => updateCompany()}>עדכן</MDBBtn>

          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    </>
  );
}
