import React from "react";
import { Bar } from "react-chartjs-2";
import { MDBContainer } from "mdbreact";

class ChartsPage extends React.Component {
  state = {
    dataBar: {
      labels: ["ינואר", "פברואר", "מרץ", "אפריל", "מאי", "יוני","יולי","אוגוסט","ספטמבר","אוקטובר","נובמבר","דצמבר"],
      datasets: [
        {
          label: "% of Votes",
          data: [12, 19, 3, 5, 2, 3, 4, 7, 18, 12, 13, 6],
          backgroundColor: [
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)" ,
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",
            "rgba(255, 134,159,0.4)",


            
            
          ],
          borderWidth: 2,
          borderColor: [
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
            "rgba(255, 134, 159, 1)",
       

          ]
        }
      ]
    },
    barChartOptions: {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        xAxes: [
          {
            barPercentage: 1,
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            }
          }
        ],
        yAxes: [
          {
            gridLines: {
              display: true,
              color: "rgba(0, 0, 0, 0.1)"
            },
            ticks: {
              beginAtZero: true
            }
          }
        ]
      }
    }
  }


  render() {
    return (
      <MDBContainer>
        <h3 className="mt-5">גרף חיובי</h3>
        <Bar data={this.state.dataBar} options={this.state.barChartOptions} />
      </MDBContainer>
    );
  }
}

export default ChartsPage;
