        let c=code.match.params.id;
        alert(c)
        useEffect(async () => {
            if(c){
                alert("האם אתה בטוח שברצונך למחוק מוצר זה?")
                removeProduct(dispatch,c);
                alert("המוצר נמחק בהצלחה");
            }
            else{
                alert("המוצר אינו נמחק נסה שנית")
            }
           
    }, [])

   
  return(
      <div></div>
  );
  };
