import { getProductById } from '../Redux/Product/ProductsThunk';
import { useParams } from 'react-router/cjs/react-router';

const  CardExample =() => {
  
  return (
    <MDBCol>
      <MDBCard style={pStyles}>
        <MDBCardImage className="img-fluid" src='http://localhost:10362/Images/${id.imageProduct}' waves />
        <MDBCardBody>
          <MDBCardTitle>שם מוצר </MDBCardTitle>
          <MDBCardText>
            Some quick example text to build on the card title and make
            up the bulk of the card&apos;s content.
          </MDBCardText>
          <MDBBtn href="#">הוסף לסל</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  )
}

export default CardExample;
const pStyles = {
  float:"right",
      margin:"auto",
      with:"100px",
      margin:'20px 3%'
      
