import React, { useEffect } from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import { useDispatch } from 'react-redux';
import { getProductById } from '../Redux/Product/ProductsThunk';
import { useParams } from 'react-router';
import { useState } from 'react';
const  CardExample =(code) => {
   const params=useParams;
  const dispatch=useDispatch();
   let c=code.match.params.id;
   let [p,setP]=useState({});

   useEffect(async () => {
   let p1=await getProductById(dispatch,c); 
    setP(p1);
  }, [])
 
    
 
  return (
    <MDBCol>
      <MDBCard style={pStyles}>
        <MDBCardImage className="img-fluid" src={`http://localhost:10362/Images/${p.imageProduct}`} />
        <MDBCardBody>
          <MDBCardTitle>{p.nameProduct}</MDBCardTitle>
          <MDBCardText>
          מידה:{p.sizeProduct},
          כמות במלאי:{p.nameProduct},
          מחיר:{p.priceProduct}
          
          </MDBCardText>
          <MDBBtn href="#">הוסף לסל</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  )
}

export default CardExample;
const pStyles = {
  float:"right",
      margin:"auto",
      with:"100px",
      margin:'20px 3%'
      
  }
