import axios from "axios"
import { useDispatch } from "react-redux";
import {GetCustomer,GetAllCustomers,AddCustomer} from './CustomerAction';
export const defaultURL="https://localhost:44314"


 export const getAllCustomersFromServer=async(dispatch)=>
{
    const customersPromise=axios.get(defaultURL+"/GetAllCustomers");
     const response=await customersPromise;
     const customers=response.data;
     dispatch(GetAllCustomers(customers))
     return customers;
 }
 

export const getCustomerFromserverById = async (dispatch,id,pass) => {
    
    const customersPromise = axios.get(defaultURL+"/GetCustomer/"+id+"%2C"+pass+"?Id="+id+"&Password="+pass);
    const response = await customersPromise;
    const customer = response.data;
    console.log(customer);
    dispatch(GetCustomer(customer))
    return customer;

}

 export const addCustomer = async(dispatch,nameCustomer,passwordCustomer,creditcardCustomer)=>
  {   
       const newCustomer={ idCustomer:5,nameCustomer: nameCustomer,passwordCustomer:passwordCustomer,creditcardCustomer:creditcardCustomer};
       const customerPromise = axios.post(defaultURL,newCustomer);
       const response = await customerPromise;
       const customer = response.data;
       dispatch(AddCustomer(customer));
       return customer;
}
