import React, { useEffect } from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';
import { useState } from 'react';
import { useDispatch } from "react-redux";
import { addCompany ,updateCompany } from '../Redux/Companies/CompaniesThunk'
import { MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';
import { useSelector } from "react-redux";


export default function SignUp (Props){
  let Company = useSelector((store) => {return store.Companies.company }); 
  debugger
  console.log("Company")
  console.log(Company)
  const [modal14, setModal14] = useState(true);
  const [errors, setErrors] = useState({ PrivateEmail: "שדה חובה", UserName: "שדה חובה", UserPassword: "שדה חובה" });
  const [company, setCompany] = useState({privateEmail:"",userName:"",userPassword:""});
  const dispatch = useDispatch();

useEffect(async () =>{
if(!Company){
Company.NameCompany="";
Company.PrivateEmail="";
Company.CustomerServiceEmail="";
Company.CustomerServiceEmailPassword="";
Company.UserName="";
Company.UserPassword="";
}
setCompany(Company);
},[])

  const newCompany = async () => {
  
     let NameCompany = document.getElementById("NameCompany").value
     let PrivateEmail = document.getElementById("PrivateEmail").value
     let CustomerServiceEmail = document.getElementById("CustomerServiceEmail").value
     let CustomerServiceEmailPassword = document.getElementById("CustomerServiceEmailPassword").value
     let UserName = document.getElementById("UserName").value
     let UserPassword = document.getElementById("UserPassword").value
     
    if(Company){
      let Customer=null;
      let ProfileCalculations=null;
      const company=await updateCompany(dispatch,Company.codeCompany,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword,Customer,ProfileCalculations) 
      if(company)
      alert("הפרטים עודכנו בהצלחה");
      setModal14(!modal14);
    }

    else{
    if (PrivateEmail && UserName && UserPassword) {
      const company = await addCompany(dispatch,NameCompany,PrivateEmail,CustomerServiceEmail,CustomerServiceEmailPassword,UserName,UserPassword);
      alert("ברוך הבא " + UserName + " לאתרינו מקווים שתהיה לך את חווית הגלישה הטובה");
      setModal14(!modal14);
      return company
    }
    else {

      alert("אנא מלא את כל השדות ");

    }}
  }

  const validEmail = (event) => {
    var myemail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if (!event.target.value)
      setErrors({ ...errors, errorEmail: 'שדה חובה' })
    else if (event.target.value.match(myemail)) { setErrors({ ...errors, errorEmail: "תקין!" }) }
    else if (!event.target.value.match(myemail)) {
      setErrors({ ...errors, errorEmail: "אימייל לא תקין" })

    }

    setCompany({ ...company, privateEmail: event.target.value })

  }
  //בדיקת תקינות של שם
  const validateName = (event) => {

    if (!event.target.value)
      setErrors({ ...errors, UserName: "שדה חובה" });
    else if (event.target.value.includes(" "))
      setErrors({ ...errors, UserName: "אין לשים רווח" });
    else {
      setErrors({ ...errors, UserName: "תקין!" });
    }
    setCompany({ ...company, UserName: event.target.value })
  }

  //בדיקת תקינות של סיסמא
  const validatePass = (event) => {

    var passw = /^[A-Za-z]\w{0,7}$/;
    if (event.target.value.match(passw)) {
      setErrors({ ...errors, pass: "תקין!" });
    }

    else {
      setErrors({ ...errors, pass: 'Wrong...!! password password between 7 to 16 characters which contain only characters, numeric digits, underscore and first character must be a letter' })
    }
    setCompany({ ...company, userPassword: event.target.value })
  }

  const up=(e)=>{
    setCompany(e.target.value)
  }
   
  
  return (
    <>
      
      <MDBContainer>
        <MDBModal isOpen={modal14} toggle={() => { setModal14(!modal14) }} centered>
          
          <MDBModalHeader toggle={() => { setModal14(!modal14); }}>
            {
              Company.codeCompany?
              <>
            טופס עדכון   
              </>
              :<>טופס הרשמה </>
            }
            </MDBModalHeader>
          <MDBModalBody>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <form>

                    <div className="grey-text">
                      <label>שם חברה</label>
                      <MDBInput  id="NameCompany" value={company.nameCompany} onChange={(e) => { up(e) }} icon="user" group type="text" validate error="wrong"
                        success="right" />
                        <label>אימייל פרטי</label>
                      <MDBInput  id="PrivateEmail" value={company.privateEmail} onChange={(e) => { validEmail(e) }} icon="envelope" group type="email" validate error="wrong"
                        success="right"  /> <p>{errors.errorEmail}</p>
                         <label>אימייל שירות לקוחות</label>
                      <MDBInput id="CustomerServiceEmail" value={company.customerServiceEmail} onChange={(e) => { up(e) }} icon="envelope" group type="email" validate error="wrong"
                        success="right" />
                        <label>סיסמאת אימייל שירות לקוחות</label>
                      <MDBInput id="CustomerServiceEmailPassword" value={company.customerServiceEmailPassword} onChange={(e) => { validatePass(e) }} icon="lock" group type="text" validate />
                     <p>{errors.pass} </p>
                      <label>שם משתמש</label>
                      <MDBInput  id="UserName" value={company.userName} icon="user" group type="text" validate error="wrong"
                        success="right" />
                      <label>סיסמאת משתמש</label>
                      <MDBInput  id="UserPassword" value={company.userPassword} onChange={(e) => { up(e) }} icon="lock" group type="text" validate  /><p>{errors.validatePass}</p>
                    </div>
                    <div className="text-center">
                    </div>
                  </form>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBModalBody>
          <MDBModalFooter>
            {
              Company.codeCompany?
            <MDBBtn color="secondary" onClick={() => newCompany()}>עדכן</MDBBtn>
            :<MDBBtn color="secondary" onClick={() => newCompany()}>הרשם</MDBBtn>
}
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    </>
  );
}
