import React, { useEffect } from "react";
import { Line } from "react-chartjs-2";
import { MDBContainer ,MDBCard,MDBCardBody} from "mdbreact";
import { useState } from 'react';
import { useSelector } from 'react-redux'; 
import { render } from "react-dom";

import {averagePos,averageNeg,averageUnsup} from '../Redux/ProfileCalculations/ProfileCalculationsThunk'


class ChartsPage extends React.Component {

  async componentDidMount() {
      
      const c=this.props.match.params.code
      const myData1 = await averagePos(c);
      this.state.dataLine.datasets[0].data=myData1
      const myData2 = await averageNeg(c);
      this.state.dataLine.datasets[1].data=myData2
      const myData3 = await averageUnsup(c);
      this.state.dataLine.datasets[2].data=myData3
      
  }

   
  state = {
    dataLine: {
      labels: ["ינואר", "פברואר", "מרץ", "אפריל", "מאי", "יוני", "יולי","אוגוסט","ספטמבר","אוקטובר","נובמבר","דצמבר"],
      datasets: [
        {
          label: "ביקורות חיוביות",
          fill: true,
          lineTension: 0.3,
          backgroundColor: "rgba(225, 204,230, .3)",
          borderColor: "rgb(205, 130, 158)",
          borderCapStyle: "butt",
          borderDash: [],
          borderDashOffset: 0.0,
          borderJoinStyle: "miter",
          pointBorderColor: "rgb(205, 130,1 58)",
          pointBackgroundColor: "rgb(255, 255, 255)",
          pointBorderWidth: 10,
          pointHoverRadius: 5,
          pointHoverBackgroundColor: "rgb(0, 0, 0)",
          pointHoverBorderColor: "rgba(220, 220, 220,1)",
          pointHoverBorderWidth: 2,
          pointRadius: 1,
          pointHitRadius: 10,
          data: []
        },
        {
          label: "בקורות שליליות",
          fill: true,
          lineTension: 0.3,
          backgroundColor: "rgba(184, 185, 210, .3)",
          borderColor: "rgb(35, 26, 136)",
          borderCapStyle: "butt",
          borderDash: [],
          borderDashOffset: 0.0,
          borderJoinStyle: "miter",
          pointBorderColor: "rgb(35, 26, 136)",
          pointBackgroundColor: "rgb(255, 255, 255)",
          pointBorderWidth: 10,
          pointHoverRadius: 5,
          pointHoverBackgroundColor: "rgb(0, 0, 0)",
          pointHoverBorderColor: "rgba(220, 220, 220, 1)",
          pointHoverBorderWidth: 2,
          pointRadius: 1,
          pointHitRadius: 10,
          data:[]
        },{
          label: "ביקורות נטרליות",
          fill: true,
          lineTension: 0.3,
          backgroundColor: "rgba(184, 185, 210, .3)",
          borderColor: "#FDB45C",
          borderCapStyle: "butt",
          borderDash: [],
          borderDashOffset: 0.0,
          borderJoinStyle: "miter",
          pointBorderColor: "#FDB45C",
          pointBackgroundColor: "rgb(255, 255, 255)",
          pointBorderWidth: 10,
          pointHoverRadius: 5,
          pointHoverBackgroundColor: "rgb(0, 0, 0)",
          pointHoverBorderColor: "rgba(220, 220, 220, 1)",
          pointHoverBorderWidth: 2,
          pointRadius: 1,
          pointHitRadius: 10,
          data:[]
        }
      ]
    }
  };

  render() {
    return (
      <MDBCard style={{}}><MDBCardBody><MDBContainer>
      <h3 className="mt-5">תרשים שנתי</h3>
      <Line class="myLine"data={this.state.dataLine} options={{ responsive: true }} />
    </MDBContainer></MDBCardBody></MDBCard>
      
    );
  }
}

export default ChartsPage;
