import React, { useEffect } from 'react';
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';
import { useDispatch } from 'react-redux';
import {  getByCategorie} from '../Redux/Product/ProductsThunk'
import { useParams } from 'react-router';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    NavLink,
    
  } from "react-router-dom";
  export default withRouter(function ShowProductsByCategory(code) { 
    const M = useSelector((store) => {return store.customers.isMenneger });
   const params=useParams;
   
  const dispatch=useDispatch();
   let c=code.match.params.categorie;
   let [p,setP]=useState([]);

   useEffect(async () => {
   let p1=await getByCategorie(dispatch,c); 
    setP(p1);
  }, [])
 
    

  return <>
    <h2 style={hstyle}>מוצרים</h2>
    {
        
        p != undefined ?
        p.length > 0 &&
        p.map((item) =>
          

        <div class="card" style={pStyles}>
                        <img src={`http://localhost:10362/Images/${item.imageProduct}`} style={{ width: "400px",height:"250px" }}
                            class="card-img-top"
                            alt="Fissure in Sandstone"
                        />
                        <div class="card-body">
                            <h5 class="card-title">{item.nameProduct}</h5>
                            <p class="card-text">
                                Some quick example text to build on the card title
                                <br></br> and make up the bulk of the
                                card's content.
                            </p>
                            
                             <NavLink to={`/OneProduct/${item.idProduct}`}>פרטים נוספים
                            </NavLink> 
                            {(M==1)&&
                            <NavLink style={nstyle} to={`/ProductUpdate/${item.idProduct}`}>עדכון 
                            </NavLink>
                         }
                        </div>
                   

                </div>
     
           
        )
        : <div>נא המתן לטעינה... </div>
        }


  </>
})
// export default ShowProductsByCategory;
const pStyles = {
  float:"right",
      marginLeft: "5px",
      marginRight: "5px",
      margin:'20px 3%'
      
  }
  const hstyle={
    textAlign:'center',
    bacroundColor:'red',
    margin:'25px 0px',
 
  }
  const nstyle={
    marginRight:'3px',
  }
