import React, { Component } from "react";
import { MDBRow, MDBCard, MDBCardBody, MDBTooltip, MDBTable, MDBTableBody, MDBTableHead, MDBInput, MDBBtn } from "mdbreact";
import { useDispatch,useSelector } from "react-redux";
import { DoInviteByBascet } from '../Redux/Invity/InvityThunk'
import {updateProductBasket} from '../Redux/Customer/CustomersThunk'
import { DeletProductFromBasket } from '../Redux/Customer/CustomerAction'
import { render } from "react-dom/cjs/react-dom.development";


export default function eCommercePage() {
  
  const dispatch=useDispatch();
  const Basket=useSelector((store)=>{return store.customers.basket});
  const P = useSelector((store) => {return store.products.products });
  const Id = useSelector((store) => { return store.customers.customer.idCustomer });
  const finalPrice=useSelector((store)=>{return store.customers.finalPriceInBasket});
  
  console.log("DoInviteByB")
         console.log(Basket)
          const DoInviteByB=async(dispatch,Basket)=>{
           debugger
                await DoInviteByBascet(dispatch,Id,Basket);
              } 

return<>

<MDBRow className="my-2" center>
      <MDBCard className="w-100">
        <MDBCardBody>
          <MDBTable className="product-table">
            <MDBTableHead className="font-weight-bold" color="mdb-color lighten-5" >
            <td><img></img></td>
    <td> <strong>שם מוצר</strong> </td>
    <td><strong>מחיר סופי </strong></td>
    <td><strong>מחיר ליחידה</strong></td>
    <td><strong>כמות </strong></td>
  <td></td>
  </MDBTableHead>
            <MDBTableBody> 
            {
          
          Basket.map((item)=>
    
      <tr>
         <td><img src={`http://localhost:10362/Images/${P[0].data.find(f=>f.idProduct==item.idProduct).imageProduct}`} alt="" className="img-fluid z-depth-0" /></td>
        <td><h5 className="mt-3"><strong>{item.nameProduct}</strong></h5></td>
        <td> <p className="text-muted">{item.priceProduct}</p></td>
        <td>{item.priceProduct}</td>

          <td><MDBInput onChange={(e)=>updateProductFromBasket1(dispatch,item,e,Basket)} type="number" value={item.amount??1} className="form-control"  id="amount1" style={{ width: "100px" }} /></td>


          <td> <strong>{item.priceProduct*item.amount}</strong></td>
        <td>
          <MDBTooltip placement="top">
              <MDBBtn color="primary" size="sm" onClick={()=> deletProductFromBasket1(dispatch,item)}>
                  X
              </MDBBtn>
              <div>הסר פריט זה מהסל</div>
          </MDBTooltip></td> 
        </tr>)}

       
   </MDBTableBody>
   <button onClick={()=>DoInviteByB(dispatch)}>בצע קנייה</button>
          </MDBTable>
          <MDBBtn rounded color="primary"   style={{marginRight:"90%"}}>סכום סופי:{finalPrice}</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBRow> 

  </>
}  

