    event.preventDefault()
   event=event.target;
     debugger
    const pInB={
      idProduct:p.idProduct,
      nameProduct:p.nameProduct,
      amount:event.value,
      priceProduct:p.priceProduct,
      finalPrice:p.priceProduct*event.value
     }
     console.log("jjjj")
     console.log(pInB);
      await updateProductBasket(dispatch,pInB,Basket);
     }
    
     const deletProductFromBasket1=(dispatch,x)=>{
        alert("מסיר מוצר זה מסל הקניות:"+ x.nameProduct);
        debugger
            dispatch(DeletProductFromBasket(x));
          }
