import { useEffect } from "react";
import { getAllProductsFromServer,getProductById,addProduct,updateProduct, removeProduct,getByCategorie} from '../Redux/Product/ProductsThunk'
import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBRow, MDBCol, MDBIcon } from 'mdbreact';


export default function ShowProducts() {

        let myProductsFromServer
        let myProduct,myUpdateProduct,myAddProduct,myRemoveProduct,myProductOfCategory;

        const [productList,setProductList]=useState ([])
        const [OneProduct,setOneProduct] = useState({})
        const [productsInThisCategorie,setproductsInThisCategorie]=useState ([])
        const dispatch=useDispatch();

        

        useEffect(async () => {
            myProductsFromServer = await getAllProductsFromServer(dispatch);
            await console.log(myProductsFromServer);
            await setProductList(myProductsFromServer);

            myProduct = await getProductById(dispatch,1);
            await console.log(myProduct);
            await setOneProduct(myProduct);
            

            myUpdateProduct = await updateProduct(dispatch,"146",",,,","1","100","ggg",20,50);
            await console.log("עדכון מוצר "+myUpdateProduct);
            await setProductList(myUpdateProduct);

            myAddProduct = await addProduct(dispatch,"146",",,,","1","300","PPPP",30,50);   
            await console.log("הוספת מוצר  "+myAddProduct);
            await setProductList(myAddProduct);
            

            myRemoveProduct = await removeProduct(dispatch,2);
            await console.log("הסרת מוצר "+myRemoveProduct);
            await setProductList(myRemoveProduct);


            myProductOfCategory = await getByCategorie(dispatch,1);
            await console.log("מוצרים לפי קטגוריה "+myProductOfCategory);
            await setproductsInThisCategorie(myProductOfCategory);
        }, [])

    return<>
     {productList.map((x)=>(      
        <MDBRow>
        <MDBCol md='4'>
          <MDBCard>
            <MDBCardImage
              top
              src='https://mdbcdn.b-cdn.net/img/Photos/Horizontal/Work/4-col/img%20%2821%29.jpg'
              overlay='white-slight'
              hover
              waves
              alt='MDBCard image cap'
            />
            <MDBCardBody className='elegant-color white-text rounded-bottom'>
              <a href='#!' className='activator waves-effect waves-light mr-4'>
                <MDBIcon icon='share-alt' className='white-text' />
              </a>
              <MDBCardTitle>{x.nameProduct}</MDBCardTitle>
              <hr className='hr-light' />
              <MDBCardText className='white-text'>
                Some quick example text to build on the card title and make up the
                bulk of the card&apos;s content.
              </MDBCardText>
              <a href='#!' className='black-text d-flex justify-content-end'>
                <h5 className='white-text'>
                  Read more
                  <MDBIcon icon='angle-double-right' className='ml-2' />
                </h5>
              </a>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
     ))}

   </>
   }
