import React from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
 import  {useState} from 'react';
 import { useDispatch } from "react-redux";
 import {addCategorie} from '../Redux/Product/ProductsThunk'

const CategorieAdd = (Props) => {
  const dispatch = useDispatch();
  const [errors, setErrors]=useState({IdCategorie:"שדה חובה"});
   const [product, setProduct]=useState(!Props.categorie?{}:Props.categorie);

   const newProducts=async(dispatch)=>{
     debugger;
     let IdCategorie=document.getElementById("c1").value
     let NameCategorie=document.getElementById("c2").value
     
    if(IdCategorie)
    {
    
     
     const categorie=await addCategorie(dispatch,IdCategorie,NameCategorie );
     alert("קטגוריה נוספה בהצלחה  "+categorie.NameCategorie)
      return categorie
    }
    else
    {
   
     alert("קוד מוצר הינו שדה חובה להוספת קטגוריה")
     
   }
   
 
   }
  
   



return (
<MDBContainer>
  <MDBRow>
    <MDBCol md="6">
      <form>
        <p className="h4 text-center mb-4">הוספת מוצר</p>
        <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
          Categorie id
        </label>
        <input type="text" id="c1" value={product.IdProduct}  className="form-control" />
    
        <br />
        <label htmlFor="defaultFormRegisteridEx" className="grey-text">
        Categorie name
        </label>
        <input type="text" id="c2" value={product.NameProduct} className="form-control" />
        <br />
        <div className="text-center mt-4">
        <br />

        
          <MDBBtn color="deep-orange" className="mb-3" type="submit" onClick={() => newProducts(dispatch)}>
          הוסף קטגוריה
                </MDBBtn>
        </div>
      </form>
    </MDBCol>
  </MDBRow>
</MDBContainer>
);
};

export default CategorieAdd;
